var events = require("events");
var path = require('path');
var jade = require('jade');
var util = require("util");

if (process.platform == 'win32') {
    var windows = require("windows");
}

// Template engine
var gen_bar = jade.compile([
    '- each item, i in sequence',
    '  - if (i != sequence.length - 1)',
    '    li(data-path="#{item.path}")',
    '      a(href="#") #{item.name}',
    '      span.divider ' + path.sep,
    '  - else',
    '    li.active(data-path="#{item.path}")',
    '      a(href="#") #{item.name}',
].join('\n'));

var gen_one_fie = jade.compile([
    'li(data-path="#{item.path}")',
    '  a(href="#") #{item.name}',
].join('\n'));

// Our real type
function AddressBar(element) {
    events.EventEmitter.call(this);
    this.element = element;

    // Monitor click on AddressBar
    var self = this;
    element.delegate('a', 'click', function () {
        self.element.children('.active').removeClass('active');
        $(this).parent().addClass('active');

        var dir = $(this).parent().attr('data-path');
        if (dir == windows.MY_COMPUTER_PATH) {
            self.emit('select drive');
        }
        else {
            self.emit('navigate', dir);
        }

        return false;
    });
}

util.inherits(AddressBar, events.EventEmitter);

AddressBar.prototype.set = function (dirPath) {
    this.currentPath = path.normalize(dirPath);

    var result = [];
    if (process.platform == 'win32') {
        // add my computer for windows
        result.push({
            name: windows.MY_COMPUTER_NAME,
            path: windows.MY_COMPUTER_PATH,
        });
    }

    // Split path into separate elements
    var sequence = this.currentPath.split(path.sep);
    for (var i = 0; i < sequence.length; ++i) {
        result.push({
            name: sequence[i],
            path: sequence.slice(0, 1 + i).join(path.sep),
        });
    }

    if (process.platform != 'win32') {
        if (sequence[0] == '') {
            // Add root for *nix
            result[0] = {
                name: 'root',
                path: '/',
            };
        }
    }
    else {
        if (result.length > 1) {
            // convert drive name to upper case for windows
            result[1].name = result[1].name.toUpperCase();
        }
    }
    this.element.html(gen_bar({ sequence: result }));
}

AddressBar.prototype.enter = function (mime) {
    // Where is current
    var how_many = this.element.children().length;
    var where = this.element.children('.active').index();
    if (where == how_many - 1) {
        // Add '/' on tail
        this.element.children().eq(-1).append('<span class="divider">/</span>');
    }
    else {
        this.element.children('li:gt(' + where + ')').remove();
    }

    // Add new folder
    this.element.append(gen_one_fie({ item: mime }));
    this.element.find('a:last').trigger('click');
}

exports.AddressBar = AddressBar;
